    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int clockspeed = Integer.parseInt(DDR.getText());
        int transfer = Integer.parseInt(PC.getText());
        String ini = Integer.toString(clockspeed);
        Clockspeed.setText(ini);
        String itu = Integer.toString(transfer);
        Transfer.setText(itu);
    }//GEN-LAST:event_jButton1ActionPerformed

