    private void DDRKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_DDRKeyTyped
        char enter=evt.getKeyChar();
        if(!(Character.isDigit(enter)))
        {
            evt.consume();
        }// TODO add your handling code here:
    }//GEN-LAST:event_DDRKeyTyped

